router.get('/:name', (req, res) => {
    var name = req.params.name;
    if (data[name] == null) {  
        res.type('text/plain');
        res.status(404);
        res.send('404 - Not Found');
    }
    else
    {
        res.render('person', { person: data[name] })
    }
    
})




router.get('/', (req, res) =>
    res.render('listing', { personlist: data }))


